mapper.update:
UPDATE t_authority ta
SET ta.enabled = 0
WHERE ta.tree_path REGEXP #{regexTreePath}

UPDATE t_authority ta
SET ta.tree_path = REPLACE(ta.tree_path, #{oldTreePath}, #{newTreePath})
WHERE ta.tree_path REGEXP #{regexTreePath}

mapper.select:
SELECT ta.* FROM t_authority ta
ta.parent_id = #{parentId}
ORDER BY sort DESC, created_at DESC

SELECT ta.*
FROM t_authority ta
WHERE ta.tree_path REGEXP #{regexTreePath}
