mapper.select:
SELECT tddi.* FROM t_data_dict tdd
LEFT JOIN t_data_dict_item tddi ON tdd.id = tddi.dict_id


    tdd.code = #{typeCode}
    AND tddi.code = #{itemCode}

LIMIT 1

SELECT tddi.* FROM t_data_dict tdd
LEFT JOIN t_data_dict_item tddi ON tdd.id = tddi.dict_id

    tdd.code = #{typeCode}
    AND tdd.enabled = #{enabled}
    AND tddi.enabled = #{enabled}

ORDER BY tddi.sort DESC, tddi.created_at DESC
